            : currentSharkTankImg.value - 1;
};

const nextSlide = () => {
    currentSharkTankImg.value =
        currentSharkTankImg.value === sharkTankGalleryImages.value.length - 1
            ? 0
            : currentSharkTankImg.value + 1;
};


